// stare.js by @jinh.kr
class stare{constructor(t,e={perspective:1e3,angle:15}){this.target=document.querySelectorAll(t),this.options=e,document.onmousemove=t=>{this.target.forEach(e=>{if(window.innerWidth>e.offsetWidth+48){let i=this.options.angle,s=e.getBoundingClientRect().y+e.offsetHeight/2,n=(s-t.pageY)/window.innerHeight*2,r=e.getBoundingClientRect().x+e.offsetWidth/2,o=(t.pageX-r)/window.innerWidth*2;e.style.transform=`rotate3d(${n}, ${o}, 0, ${i*(n**2+o**2)}deg)`,e.parentNode.style.perspective=`${this.options.perspective}px`}else this.target.forEach(t=>{t.style.transform="rotate3d(0, 0, 0, 0)"})})}}perspective(t){return this.options.perspective=null!=t?t:1e3,this}angle(t){return this.options.angle=null!=t?t:15,this}}